var winston = require('winston');
var config = require('../config.json');
winston.emitErrs = true;

var logger = new winston.Logger({
    transports: [
        new winston.transports.File({
            level: config.general.logger.file.level,
            filename: config.general.logger.file.location,
            silent: config.general.logger.file.disabled,
            handleExceptions: true,
            json: true,
            maxsize: 5242880, //5MB
            maxFiles: 5,
            colorize: false
        }),
        new winston.transports.Console({
            level: config.general.logger.console.level,
            silent: config.general.logger.console.disabled,
            handleExceptions: true,
            json: true,
            colorize: true
        })
    ],
    exitOnError: false
});

module.exports = logger;
module.exports.stream = {
    write: function(message, encoding){
        logger.info(message);
    }
};